
public class WriteSquares {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//1 squared
		System.out.println(1 + " squared = " + (1 * 1));
		
		//2 squared
		System.out.println(1 + " squared = " + (2 * 2));
		
		//3 squared
		System.out.println(3 + " squared = " + (3 * 3));
		
		//4 squared
		System.out.println(4 + " squared = " + (4 * 4));
		
		//5 squared
		System.out.println(5 + " squared = " + (5 * 5));
		
		//For loop for squaring numbers
		for(int i = 1; i <=5; i++){
			System.out.println(i + " squared = " + (i*i));
		}
		
		System.out.println("I eat " + 3 +" times a day");
		
		double grade = (95.1 + 71.9 + 82.6) / 3.0;
		System.out.println("Your grade is: " + grade);
		
		int students = 11 + 17 + 4 + 19 + 14; 
		System.out.println("There are " + students + " students in the classroom");
	} 

}
