
public class time {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Declare variables
		int hours = 365 * 24;
		int minutes = hours * 60;
		int seconds = minutes * 60;
		
		//Print calculation of how many hours in a year
		System.out.println("The number of hours in a year are: " + hours);
		
		//Print calculation of how many minutes in a year there are
		System.out.println("The number of minutes in a year are: " + minutes);
		
		//Print calculation of how many seconds in a year there are
		System.out.println("The number of seconds in a year are: " + seconds);
	}

}
