public class charatPractice {

	//Practice w/ the charAt method
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String major = "CSE";
		for (int i = 0; i < major.length(); i++){
			char c = major.charAt(i);
			System.out.println(c);
		}
	}

}
